/**=========================================================
* Module: clear-storage.js
* Removes a key from the browser storage via element click
=========================================================*/
(function () {
    'use strict';

    angular.module('app.utils').directive('resetKey', resetKey);
    resetKey.$inject = ['$state', '$localStorage'];

    function resetKey($state, $localStorage) {
        return {
            link: link,
            restrict: 'A',
            scope: {
                resetKey: '@'
            }
        };

        function link(scope, element) {
            element.on('click', function (e) {
                e.preventDefault();
                if (scope.resetKey) {
                    delete $localStorage[scope.resetKey];
                    $state.go($state.current, {}, { reload: true });
                } else {
                    $.error('No storage key specified for reset.');
                }
            });
        }
    }
})();
/**=========================================================
* Module: load-css.js
* Request and load into the current page a css file
=========================================================*/
(function () {
    'use strict';

    angular.module('app.utils').directive('loadCss', loadCss);

    function loadCss() {
        return {
            link: link,
            restrict: 'A'
        };

        function link(scope, element, attrs) {
            element.on('click', function (e) {
                if (element.is('a')) {
                    e.preventDefault();
                }
                const uri = attrs.loadCss;
                let link;
                if (uri) {
                    link = createLink(uri);
                    if (!link) {
                        $.error('Error creating stylesheet link element.');
                    }
                } else {
                    $.error('No stylesheet location defined.');
                }
            });
        }

        function createLink(uri) {
            const linkId = 'autoloaded-stylesheet';
            const ele = $('#' + linkId);
            const oldLink = ele.attr('id', linkId + '-old');
            $('head').append($('<link/>').attr({
                'id': linkId,
                'rel': 'stylesheet',
                'href': uri
            }));
            if (oldLink.length) {
                oldLink.remove();
            }
            return ele;
        }
    }
})();
/**=========================================================
* Module: now.js
* Provides a simple way to display the current time formatted
=========================================================*/
(function () {
    'use strict';

    angular.module('app.utils').directive('now', now);
    now.$inject = ['dateFilter', '$interval'];

    function now(dateFilter, $interval) {
        return {
            link: link,
            restrict: 'EA'
        };

        function link(scope, element, attrs) {
            const format = attrs.format;

            function updateTime() {
                const dt = dateFilter(new Date(), format);
                element.text(dt);
            }
            updateTime();
            const intervalPromise = $interval(updateTime, 1000);
            scope.$on('$destroy', function () {
                $interval.cancel(intervalPromise);
            });
        }
    }
})();
